                    resp = `${option.type}`;
                  }
                } else if (option.floorElement === "RestRooms") {
                  resp = `${option.type} Washroom`;
                } else {
                  resp =  "";
                }

               return <div className="mapped-data m-0 " style={{padding:"0"}}>
                    <div
                        className="row w-100"
                    >
                        <div className="col-2">
                            <div>
                                <img src="/inclunav/assets/images/destination2.svg" alt="destination" />
                            </div>

                        </div>
